mapper.delete:
	UPDATE EDU_BOARD_COM
	   SET DEL_DATE = SYSDATE
 	 WHERE COM_SEQ = #{comSeq}
 	   AND BRD_SEQ = #{brdSeq}
	   AND COM_GROUP = #{comGroup}

	UPDATE EDU_BOARD_COM
	   SET DEL_DATE = SYSDATE
 	 WHERE COM_GROUP = #{comGroup}
 	   AND BRD_SEQ = #{brdSeq}

mapper.update:
UPDATE EDU_BOARD_COM
   SET COM_CONTENT = #{comContent}
 WHERE COM_SEQ = #{comSeq}

UPDATE EDU_BOARD_COM
   SET COM_ORDER = COM_ORDER  + 1
 WHERE COM_GROUP = #{comGroup}
   AND COM_ORDER >=#{comOrder}
